// API para consumir dados da Genove
import axios from 'axios';
import { parseString } from 'react-native-xml2js';

const API_URL = 'https://api-genove.agcodecraft.com/cp';

export const fetchGenoveProducts = async () => {
  try {
    const response = await axios.get(API_URL, {
      responseType: 'text',
    });

    return new Promise((resolve, reject) => {
      parseString(response.data, (err, result) => {
        if (err) {
          reject(err);
          return;
        }

        const items = result.rss.channel[0].item || [];
        const products = items.map(item => ({
          id: item.codigo ? item.codigo[0] : '',
          title: item.title ? item.title[0] : '',
          price: item.preco ? item.preco[0] : '',
          image: item.link_imagem ? item.link_imagem[0] : '',
          availability: item.disponibilidade ? item.disponibilidade[0] : '',
          brand: item.marca ? item.marca[0] : '',
          link: item.link ? item.link[0] : '',
          store: 'Genove'
        }));

        resolve(products);
      });
    });
  } catch (error) {
    console.error('Error fetching Genove products:', error);
    throw error;
  }
};
