import React, { useState, useEffect, useRef } from 'react';
import { 
  View, 
  Text, 
  FlatList, 
  Image, 
  StyleSheet, 
  TouchableOpacity,
  ActivityIndicator,
  TextInput,
  StatusBar,
  Dimensions,
  ScrollView,
  Animated
} from 'react-native';
import { fetchGenoveProducts } from './genoveApiReal';

const { width } = Dimensions.get('window');

// Cores do tema - Paleta moderna
const COLORS = {
  primary: '#1E3A8A',
  primaryLight: '#2563EB',
  accent: '#F43F5E',
  background: '#F9FAFB',
  card: '#FFFFFF',
  text: '#1F2937',
  textLight: '#6B7280',
  border: '#E5E7EB',
  success: '#10B981',
  error: '#EF4444',
  searchBg: '#F3F4F6',
  shadow: 'rgba(0,0,0,0.1)'
};

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [loadingMore, setLoadingMore] = useState(false);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [sortOption, setSortOption] = useState('default');
  const [showSearch, setShowSearch] = useState(false);
  const [pagination, setPagination] = useState({
    currentPage: 1,
    lastPage: 1,
    total: 0,
    hasNextPage: false,
    hasPrevPage: false
  });

  // Refer√™ncia para o FlatList
  const flatListRef = useRef(null);

  // Valor de anima√ß√£o para o cabe√ßalho fixo
  const scrollY = useRef(new Animated.Value(0)).current;
  const headerHeight = 60;

  // Calcula a opacidade do cabe√ßalho fixo com base na posi√ß√£o de rolagem
  const headerOpacity = scrollY.interpolate({
    inputRange: [0, 100],
    outputRange: [0, 1],
    extrapolate: 'clamp'
  });

  useEffect(() => {
    loadProducts();
  }, []);

  // Efeito para rolar para o topo quando os produtos s√£o atualizados
  useEffect(() => {
    if (filteredProducts.length > 0) {
      goToTop();
    }
  }, [filteredProducts]);

  const loadProducts = async (page = 1, query = searchQuery, sort = sortOption) => {
    try {
      setLoading(page === 1);
      if (page > 1) setLoadingMore(true);

      // Aqui adicionamos os par√¢metros de busca e ordena√ß√£o para a API
      const result = await fetchGenoveProducts(page, query, sort);

      // Sempre substituir os produtos ao mudar de p√°gina
      setProducts(result.products);
      setFilteredProducts(result.products);

      setPagination(result.pagination);
      setLoading(false);
      setLoadingMore(false);
    } catch (err) {
      setError('Falha ao carregar produtos: ' + err.message);
      setLoading(false);
      setLoadingMore(false);
    }
  };

  const goToPage = (page) => {
    if (page >= 1 && page <= pagination.lastPage && page !== pagination.currentPage) {
      loadProducts(page);
      // Rolar para o topo ao mudar de p√°gina
      goToTop();
    }
  };

  // Fun√ß√£o para rolar para o topo quando a p√°gina for atualizada
  const goToTop = () => {
    if (flatListRef.current) {
      flatListRef.current.scrollToOffset({ offset: 0, animated: true });
    }
  };

  // Fun√ß√£o para lidar com a mudan√ßa no campo de busca
  const handleSearchChange = (text) => {
    setSearchQuery(text);
    // Recarregar produtos com a nova busca
    loadProducts(1, text, sortOption);
  };

  // Fun√ß√£o para lidar com a mudan√ßa na ordena√ß√£o
  const handleSortChange = (option) => {
    setSortOption(option);
    // Recarregar produtos com a nova ordena√ß√£o
    loadProducts(1, searchQuery, option);
  };

  const renderItem = ({ item }) => {
    const isAvailable = item.availability !== 'fora de estoque';

    return (
      <TouchableOpacity style={styles.productCard}>
        <Image 
          source={{ uri: item.image }} 
          style={styles.productImage}
        />
        <View style={styles.productInfo}>
          <Text style={styles.productTitle} numberOfLines={2}>{item.title}</Text>
          <Text style={styles.productBrand}>{item.brand}</Text>
          <Text style={styles.productPrice}>{item.price}</Text>
          <View style={styles.bottomRow}>
            <View style={[
              styles.productAvailability,
              isAvailable ? styles.available : styles.unavailable
            ]}>
              <Text style={styles.availabilityText}>
                {isAvailable ? 'Dispon√≠vel' : 'Fora de estoque'}
              </Text>
            </View>
            <Text style={styles.storeName}>{item.store}</Text>
          </View>
        </View>
      </TouchableOpacity>
    );
  };

  const renderFooter = () => {
    return (
      <View>
        {loadingMore && (
          <View style={styles.loadingMore}>
            <ActivityIndicator size="small" color={COLORS.accent} />
            <Text style={styles.loadingMoreText}>Carregando mais produtos...</Text>
          </View>
        )}

        <View style={styles.paginationContainer}>
          <TouchableOpacity 
            style={[styles.pageButton, !pagination.hasPrevPage && styles.pageButtonDisabled]}
            onPress={() => goToPage(pagination.currentPage - 1)}
            disabled={!pagination.hasPrevPage}
          >
            <Text style={[styles.pageButtonText, !pagination.hasPrevPage && styles.pageButtonTextDisabled]}>
              {'<'}
            </Text>
          </TouchableOpacity>

          <Text style={styles.paginationText}>
            P√°gina {pagination.currentPage} de {pagination.lastPage}
          </Text>

          <TouchableOpacity 
            style={[styles.pageButton, !pagination.hasNextPage && styles.pageButtonDisabled]}
            onPress={() => goToPage(pagination.currentPage + 1)}
            disabled={!pagination.hasNextPage}
          >
            <Text style={[styles.pageButtonText, !pagination.hasNextPage && styles.pageButtonTextDisabled]}>
              {'>'}
            </Text>
          </TouchableOpacity>
        </View>
      </View>
    );
  };

  const ListHeaderComponent = () => (
    <View>
      <View style={styles.header}>
        <Image 
          source={require('./assets/logo.png')} 
          style={styles.logoImage}
          resizeMode="contain"
        />
      </View>
      <View style={styles.searchContainer}>
        <View style={styles.searchInputContainer}>
          <TextInput
            style={styles.searchInput}
            placeholder="Buscar produtos..."
            value={searchQuery}
            onChangeText={handleSearchChange}
            placeholderTextColor={COLORS.textLight}
          />
          {searchQuery.length > 0 && (
            <TouchableOpacity 
              style={styles.clearButton} 
              onPress={() => handleSearchChange('')}
            >
              <Text style={styles.clearButtonText}>‚úï</Text>
            </TouchableOpacity>
          )}
        </View>

        <View style={styles.sortContainer}>
          <Text style={styles.sortLabel}>Ordenar por:</Text>
          <ScrollView 
            horizontal 
            showsHorizontalScrollIndicator={true} 
            style={styles.sortOptionsScroll}
            contentContainerStyle={styles.sortOptionsContent}
            indicatorStyle="white"
          >
            <TouchableOpacity 
              style={[styles.sortOption, sortOption === 'default' && styles.sortOptionActive]} 
              onPress={() => handleSortChange('default')}
            >
              <Text style={[styles.sortOptionText, sortOption === 'default' && styles.sortOptionTextActive]}>Padr√£o</Text>
            </TouchableOpacity>
            <TouchableOpacity 
              style={[styles.sortOption, sortOption === 'price-high-low' && styles.sortOptionActive]} 
              onPress={() => handleSortChange('price-high-low')}
            >
              <Text style={[styles.sortOptionText, sortOption === 'price-high-low' && styles.sortOptionTextActive]}>Maior Pre√ßo</Text>
            </TouchableOpacity>
            <TouchableOpacity 
              style={[styles.sortOption, sortOption === 'price-low-high' && styles.sortOptionActive]} 
              onPress={() => handleSortChange('price-low-high')}
            >
              <Text style={[styles.sortOptionText, sortOption === 'price-low-high' && styles.sortOptionTextActive]}>Menor Pre√ßo</Text>
            </TouchableOpacity>
            <TouchableOpacity 
              style={[styles.sortOption, sortOption === 'a-z' && styles.sortOptionActive]} 
              onPress={() => handleSortChange('a-z')}
            >
              <Text style={[styles.sortOptionText, sortOption === 'a-z' && styles.sortOptionTextActive]}>A-Z</Text>
            </TouchableOpacity>
            <TouchableOpacity 
              style={[styles.sortOption, sortOption === 'z-a' && styles.sortOptionActive]} 
              onPress={() => handleSortChange('z-a')}
            >
              <Text style={[styles.sortOptionText, sortOption === 'z-a' && styles.sortOptionTextActive]}>Z-A</Text>
            </TouchableOpacity>
          </ScrollView>
        </View>
      </View>
      {!loading && !error && (
        <View style={styles.resultsInfo}>
          <Text style={styles.resultsText}>
            {pagination.total} produtos encontrados
          </Text>
        </View>
      )}
    </View>
  );

  if (loading) {
    return (
      <View style={styles.centered}>
        <ActivityIndicator size="large" color={COLORS.accent} />
        <Text style={styles.loadingText}>Carregando produtos...</Text>
      </View>
    );
  }

  if (error) {
    return (
      <View style={styles.centered}>
        <Text style={styles.errorText}>{error}</Text>
        <TouchableOpacity style={styles.retryButton} onPress={() => loadProducts()}>
          <Text style={styles.retryButtonText}>Tentar novamente</Text>
        </TouchableOpacity>
      </View>
    );
  }

  const toggleSearch = () => {
    setShowSearch(!showSearch);
  };

  const handleBack = () => {
    // Implementar navega√ß√£o de volta
    console.log('Voltar');
  };

  const handleFilterPress = () => {
    // Mostrar modal de filtros
    console.log('Abrir filtros');
  };

  return (
    <View style={styles.container}>
      <StatusBar barStyle="light-content" backgroundColor={COLORS.primary} />

      {/* Cabe√ßalho fixo que aparece ao rolar */}
      <Animated.View 
        style={[
          styles.fixedHeader, 
          { opacity: headerOpacity }
        ]}
      >
        <TouchableOpacity style={styles.backButton} onPress={handleBack}>
          <Text style={styles.backButtonText}>‚Üê</Text>
        </TouchableOpacity>

        <Image 
          source={require('./assets/logo.png')} 
          style={styles.smallLogo}
          resizeMode="contain"
        />

        <View style={styles.headerIcons}>
          <TouchableOpacity style={styles.headerIcon} onPress={toggleSearch}>
            <Text style={styles.iconText}>üîç</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.headerIcon} onPress={handleFilterPress}>
            <Text style={styles.iconText}>‚â°</Text>
          </TouchableOpacity>
        </View>
      </Animated.View>

      {/* Barra de pesquisa que aparece quando showSearch √© true */}
      {showSearch && (
        <View style={styles.searchOverlay}>
          <View style={styles.searchInputContainer}>
            <TextInput
              style={styles.searchInput}
              placeholder="Buscar produtos..."
              value={searchQuery}
              onChangeText={handleSearchChange}
              placeholderTextColor={COLORS.textLight}
              autoFocus
            />
            <TouchableOpacity 
              style={styles.clearButton} 
              onPress={() => toggleSearch()}
            >
              <Text style={styles.clearButtonText}>‚úï</Text>
            </TouchableOpacity>
          </View>
        </View>
      )}

      <FlatList
        ref={flatListRef}
        data={filteredProducts}
        renderItem={renderItem}
        keyExtractor={item => item.id}
        contentContainerStyle={styles.list}
        ListHeaderComponent={ListHeaderComponent}
        stickyHeaderIndices={[0]}
        ListFooterComponent={renderFooter}
        ListEmptyComponent={
          <View style={styles.noResults}>
            <Text style={styles.noResultsText}>Nenhum produto encontrado</Text>
          </View>
        }
        onScroll={Animated.event(
          [{ nativeEvent: { contentOffset: { y: scrollY } } }],
          { useNativeDriver: false }
        )}
        scrollEventThrottle={16}
      />
    </View>
  );
};

