import axios from 'axios';
import { parseString } from 'react-native-xml2js';
import { promisify } from 'util';

const parseXml = promisify(parseString);
const API_URL = 'https://api-genove.agcodecraft.com/cp';

export const fetchGenoveProducts = async () => {
  try {
    const response = await axios.get(API_URL, {
      responseType: 'text',
      headers: {
        Accept: 'application/xml',
        'Content-Type': 'application/xml',
      },
      timeout: 10000,
    });

    const result = await parseXml(response.data);

    const items = result?.rss?.channel?.[0]?.item || [];

    const products = items.map((item) => ({
      id: item.codigo?.[0] || '',
      title: item.title?.[0] || '',
      price: item.preco?.[0] || '',
      price_normal: item.preco_normal_sem_liquidacao?.[0] || '',
      image: item.link_imagem?.[0] || '',
      availability: item.disponibilidade?.[0] || '',
      brand: item.marca?.[0] || '',
      saleType: item.tipo_venda?.[0] || '',
      link: item.link?.[0] || '',
      store: 'Genove',
    }));

    return products;
  } catch (error) {
    console.error('Erro ao buscar produtos da Genove:', error.message);
    return [];
  }
};
