import React, { useState, useEffect, useRef } from 'react';
import { 
  View, 
  Text, 
  FlatList, 
  Image, 
  StyleSheet, 
  TouchableOpacity,
  ActivityIndicator,
  TextInput,
  StatusBar,
  Dimensions
} from 'react-native';
import { fetchGenoveProducts } from './genoveApiReal';

const { width } = Dimensions.get('window');

// Cores do tema
const COLORS = {
  primary: '#143447',
  accent: '#FF0763',
  background: '#F8F9FA',
  card: '#FFFFFF',
  text: '#333333',
  textLight: '#666666',
  border: '#E0E0E0',
  success: '#2a9d8f',
  error: '#e63946',
  searchBg: '#E8EDF1'
};

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [loadingMore, setLoadingMore] = useState(false);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchInput, setSearchInput] = useState('');
  const [sortOption, setSortOption] = useState('default');
  const [pagination, setPagination] = useState({
    currentPage: 1,
    lastPage: 1,
    total: 0,
    hasNextPage: false,
    hasPrevPage: false
  });

  useEffect(() => {
    loadProducts();
  }, []);

  useEffect(() => {
    if (products.length > 0) {
      filterProducts();
    }
  }, [searchQuery, products, sortOption]);

  const loadProducts = async (page = 1) => {
    try {
      setLoading(page === 1);
      if (page > 1) setLoadingMore(true);

      const result = await fetchGenoveProducts(page);

      // Sempre substituir os produtos ao mudar de página
      setProducts(result.products);

      setPagination(result.pagination);
      setLoading(false);
      setLoadingMore(false);
    } catch (err) {
      setError('Falha ao carregar produtos: ' + err.message);
      setLoading(false);
      setLoadingMore(false);
    }
  };

  const goToPage = (page) => {
    if (page >= 1 && page <= pagination.lastPage && page !== pagination.currentPage) {
      loadProducts(page);
      // Rolar para o topo ao mudar de página
      goToTop();
    }
  };

  const filterProducts = () => {
    if (!searchQuery.trim()) {
      setFilteredProducts(products);
      return;
    }

    const query = searchQuery.toLowerCase();
    const filtered = products.filter(product => 
      product.title.toLowerCase().includes(query) || 
      product.brand.toLowerCase().includes(query)
    );
    setFilteredProducts(filtered);
  };

  // Referência para o FlatList
  const flatListRef = useRef(null);

  // Função para rolar para o topo quando a página for atualizada
  const goToTop = () => {
    if (flatListRef.current) {
      flatListRef.current.scrollToOffset({ offset: 0, animated: true });
    }
  };

  // Efeito para rolar para o topo quando os produtos são atualizados
  useEffect(() => {
    if (filteredProducts.length > 0) {
      goToTop();
    }
  }, [filteredProducts]);

  const renderItem = ({ item }) => {
    const isAvailable = item.availability !== 'fora de estoque';

    return (
      <TouchableOpacity style={styles.productCard}>
        <Image 
          source={{ uri: item.image }} 
          style={styles.productImage}
        />
        <View style={styles.productInfo}>
          <Text style={styles.productTitle} numberOfLines={2}>{item.title}</Text>
          <Text style={styles.productBrand}>{item.brand}</Text>
          <Text style={styles.productPrice}>{item.price}</Text>
          <View style={styles.bottomRow}>
            <Text style={[
              styles.productAvailability,
              isAvailable ? styles.available : styles.unavailable
            ]}>
              {isAvailable ? 'Disponível' : 'Fora de estoque'}
            </Text>
            <Text style={styles.storeName}>{item.store}</Text>
          </View>
        </View>
      </TouchableOpacity>
    );
  };

  const renderFooter = () => {
    return (
      <View>
        {loadingMore && (
          <View style={styles.loadingMore}>
            <ActivityIndicator size="small" color={COLORS.accent} />
            <Text style={styles.loadingMoreText}>Carregando mais produtos...</Text>
          </View>
        )}

        <View style={styles.paginationContainer}>
          <TouchableOpacity 
            style={[styles.pageButton, !pagination.hasPrevPage && styles.pageButtonDisabled]}
            onPress={() => goToPage(pagination.currentPage - 1)}
            disabled={!pagination.hasPrevPage}
          >
            <Text style={[styles.pageButtonText, !pagination.hasPrevPage && styles.pageButtonTextDisabled]}>
              {'<'}
            </Text>
          </TouchableOpacity>

          <Text style={styles.paginationText}>
            Página {pagination.currentPage} de {pagination.lastPage}
          </Text>

          <TouchableOpacity 
            style={[styles.pageButton, !pagination.hasNextPage && styles.pageButtonDisabled]}
            onPress={() => goToPage(pagination.currentPage + 1)}
            disabled={!pagination.hasNextPage}
          >
            <Text style={[styles.pageButtonText, !pagination.hasNextPage && styles.pageButtonTextDisabled]}>
              {'>'}
            </Text>
          </TouchableOpacity>
        </View>
      </View>
    );
  };

  const ListHeaderComponent = () => (
    <View>
      <View style={styles.header}>
        <Image 
          source={require('./assets/logo.png')} 
          style={styles.logoImage}
          resizeMode="contain"
        />
      </View>
      <View style={styles.searchContainer}>
        <View style={styles.searchInputContainer}>
          <TextInput
            style={styles.searchInput}
            placeholder="Buscar produtos..."
            value={searchQuery}
            onChangeText={setSearchQuery}
            placeholderTextColor="#999"
          />
          {searchQuery.length > 0 && (
            <TouchableOpacity 
              style={styles.clearButton} 
              onPress={() => setSearchQuery('')}
            >
              <Text style={styles.clearButtonText}>✕</Text>
            </TouchableOpacity>
          )}
        </View>
      </View>
      {!loading && !error && (
        <View style={styles.resultsInfo}>
          <Text style={styles.resultsText}>
            {pagination.total} produtos encontrados
          </Text>
        </View>
      )}
    </View>
  );

  if (loading) {
    return (
      <View style={styles.centered}>
        <ActivityIndicator size="large" color={COLORS.accent} />
        <Text style={styles.loadingText}>Carregando produtos...</Text>
      </View>
    );
  }

  if (error) {
    return (
      <View style={styles.centered}>
        <Text style={styles.errorText}>{error}</Text>
        <TouchableOpacity style={styles.retryButton} onPress={() => loadProducts()}>
          <Text style={styles.retryButtonText}>Tentar novamente</Text>
        </TouchableOpacity>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <StatusBar barStyle="light-content" backgroundColor={COLORS.primary} />

      <FlatList
        ref={flatListRef}
        data={filteredProducts}
        renderItem={renderItem}
        keyExtractor={item => item.id}
        contentContainerStyle={styles.list}
        ListHeaderComponent={ListHeaderComponent}
        stickyHeaderIndices={[0]}
        ListFooterComponent={renderFooter}
        ListEmptyComponent={
          <View style={styles.noResults}>
            <Text style={styles.noResultsText}>Nenhum produto encontrado</Text>
          </View>
        }
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: COLORS.background,
  },
  header: {
    backgroundColor: COLORS.primary,
    padding: 0,
    alignItems: 'center',
    justifyContent: 'center',
  },
  logoImage: {
    height: 80,
    width: width,
  },
  searchContainer: {
    backgroundColor: COLORS.primary,
    paddingHorizontal: 16,
    paddingVertical: 12,
    paddingBottom: 16,
    borderBottomLeftRadius: 16,
    borderBottomRightRadius: 16,
  },
  searchInputContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: COLORS.searchBg,
    borderRadius: 24,
    paddingHorizontal: 12,
  },
  searchInput: {
    flex: 1,
    height: 44,
    fontSize: 16,
    color: COLORS.text,
    paddingHorizontal: 8,
  },
  clearButton: {
    width: 24,
    height: 24,
    borderRadius: 12,
    backgroundColor: COLORS.textLight,
    justifyContent: 'center',
    alignItems: 'center',
  },
  clearButtonText: {
    fontSize: 12,
    color: '#fff',
    fontWeight: 'bold',
  },
  resultsInfo: {
    padding: 12,
    backgroundColor: COLORS.background,
  },
  resultsText: {
    fontSize: 14,
    color: COLORS.textLight,
    fontStyle: 'italic',
  },
  list: {
    padding: 12,
  },
  productCard: {
    flexDirection: 'row',
    backgroundColor: COLORS.card,
    borderRadius: 12,
    overflow: 'hidden',
    marginBottom: 16,
    elevation: 3,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
  },
  productImage: {
    width: 120,
    height: 140,
    resizeMode: 'cover',
    backgroundColor: '#f0f0f0'
  },
  productInfo: {
    flex: 1,
    padding: 14,
    justifyContent: 'space-between',
  },
  productTitle: {
    fontSize: 16,
    fontWeight: 'bold',
    color: COLORS.primary,
